import { useState } from 'react';

import data from "./data.json";


function Winner({fine,risposta}){
  if(fine){
    if(risposta==="meloni")
      return <p> hai indovinato</p>
    else
      return <p> hai sbagliato</p>
  }

}

function Opzione({risposta,statoQuiz,option}){
  return(
  <>
      <input
      type="radio"
      value={risposta}
      checked={answer === risposta}
      onChange={option}
      disabled={statoQuiz===1}
      
    /><label>{risposta}</label><br/>
  </>)
}

function Domanda({domanda,risposte,corretta,statoQuiz,option}){
  return (
    <>
      <p>{domanda}</p>
      {risposte.map((risposte,i)=>{
      <Opzione 
        risposta={risposte[i]} 
        statoQuiz={statoQuiz}
        option={option}
         />
    })}
      <Opzione risposta/>
    </>
  )
}



