import { useState } from 'react';

import data from "./data.json";

const dataset=[{
  "id":"0",
  "domanda":"chi è il presidente?",
  "risposte":["meloni","mattarella","salvini"],
  "corretta":"meloni"
},
{
  "id":"1",
  "domanda":"In quale anno finirà il mondo?",
  "risposte":["2012","2025","1990"],
  "corretta":"2012"
},
{
  "id":"2",
  "domanda":"di che colore è il sole?",
  "risposte":["giallo","verde","rosso"],
  "corretta":"giallo"
}
]



function Winner({fine,risposta}){
  if(fine){
    if(risposta==="meloni")
      return <p> hai indovinato</p>
    else
      return <p> hai sbagliato</p>
  }

}


function Domande({key,domande,index,answer, option,final}){

  return (
    <>
      <input
      type="radio"
      value={domande[index]}
      checked={answer === domande[index]}
      onChange={option}
      disabled={final===1}
      
    /><label>{domande[index]}</label><br/>
    </>
  )
  // return(
  // <>
  //   <p>{dataset[id].domanda}</p>
  //   <input
  //     type="radio"
  //     value={dataset[id].risposte[0]}
  //     checked={answer === dataset[id].risposte[0]}
  //     onChange={onOptionChange}
  //     disabled={fine===1}
  //   /><label>dataset[id].risposte[0]</label><br/>

  //   <input
  //     type="radio"
  //     value={dataset[id].risposte[1]}
  //     checked={answer === dataset[id].risposte[1]}
  //     onChange={onOptionChange}
  //     disabled={fine===1}
  //   /><label>dataset[id].risposte[1]</label><br/>

  //   <input
  //     type="radio"
  //     value={dataset[id].risposte[2]}
  //     checked={answer === dataset[id].risposte[2]}
  //     onChange={onOptionChange}
  //     disabled={fine===1}
  //   /><label>dataset[id].risposte[2]</label><br/>
  // </>)
}

export default function Quiz() {

  

  const [answer,SetAnswer] = useState("")
  const [idDomanda,setidDomanda] = useState(0)
  const [fine,setFine] = useState(0)

  const onOptionChange = e => {
    SetAnswer(e.target.value)
  }

  function renderDomanda(){
    return data.domande.map((domande,index) => (
      <Domande key={index} domande={domande} index={index} answer={answer} option={onOptionChange} final={fine} />

    )
    )
  }

    return <>
  
    {renderDomanda()}
    <input
      type="button"
      value="Clicca qui per controllare"
      onClick={() => {setFine(1)}} 
    />
    <br />
    <input
      type="button"
      value="AVANTI"
      disabled={idDomanda===2}
      onClick={() => {setidDomanda(idDomanda+1)}} 
    />
        <input
      type="button"
      value="INDIETRO"
      disabled={idDomanda===0}
      onClick={() => {setidDomanda(idDomanda-1)}} 
    />

    <p> Selected answer <strong>{answer}</strong></p>
    <p> stato: <strong>{fine}</strong></p>

    <Winner fine={fine} risposta={answer}/>
    
    </>;
  }
