import { useState } from 'react';



import QuizList from './QuizList';
import Domande from './Domande'


function Winner({fine,risposte}){
  var punti =0
  var errori= []
  if(fine){
    for (var i=0;i<risposte.length;i++){
      if (risposte[i]===data.domande[i].corretta)
        punti++;
      else
        errori.push(<p>la domanda {i+1} è sbagliata, la risposta corretta è: <b>{data.domande[i].corretta}</b> </p>)
    }
    return <>
    <p> Totalizzati {punti} punti</p>
    {errori.map((errore)=> {return errore })}
    </>
  }


}




export default function Quiz() {
  // QUIZ IN USO
  const [quiz,setQuiz] = useState(0)





  function changeQuiz(nquiz) {
      //IMPOSTO UN NUOVO QUIZ
      console.log("IMPOSTO IL QUIZ....."+nquiz)
      setQuiz(nquiz)
  }
    
    return <>
      <table style={{width:"100%"}}>
      <tbody>
        <tr>
          <td style={{width:"75%"}}>
            {/*QUI INSERISCO LA RENDERIZZAZIONE DEL QUIZ*/}

            <Domande/>
          
            
          </td>
          <td>
            {/*QUI INSERISCO L'ELENCO DEI QUIZ*/}
            <h1> SELEZIONA UN QUIZ</h1>
            <QuizList
            onClickHandling={changeQuiz}
            />
          </td>
        </tr>
        </tbody>
      </table>

    </>;
  }
