import { useState } from 'react';

function Winner({fine,risposta}){
  if(fine){
    if(risposta==="meloni")
      return <p> hai indovinato</p>
    else
      return <p> hai sbagliato</p>
  }

}

export default function Quiz() {

  const [answer,SetAnswer] = useState("")
  const [fine,setFine] = useState(0)

  const onOptionChange = e => {
    SetAnswer(e.target.value)
  }

    return <>
    
    <input
      type="radio"
      value="meloni"
      checked={answer === "meloni"}
      onChange={onOptionChange}
      disabled={answer==="meloni"}
    /><label>Meloni</label><br/>

    <input
      type="radio"
      value="mattarella"
      checked={answer === "mattarella"}
      onChange={onOptionChange}
      disabled={fine==="1"}
    /><label>Mattarella</label><br/>

    <input
      type="radio"
      value="salvini"
      checked={answer === "salvini"}
      onChange={onOptionChange}
      disabled={fine==="1"}
    /><label>Salvini</label><br/>

    <input
      type="button"
      value="Clicca qui per controllare"
      onClick={() => {setFine(1)}} 
    />
    <p> Selected answer <strong>{answer}</strong></p>
    <p> stato: <strong>{fine}</strong></p>

    <Winner fine={fine} risposta={answer}/>
    
    </>;
  }
